const jisho = require("./jisho");
const sqlite = require("./sqlite");

module.exports = function run(input, flags, cb) {

  sqlite(function (err, dbMethods) {
    if (err) {
      console.log("Something wrong happened while opening the kanji database.");
      console.log(err);
      process.exit(1);
    }
    jisho(input, flags, dbMethods, function (reuslt) {
      if (cb) cb(reuslt)
    })

  });
};
